const pos = world.getPosition(bot);
const cx = Math.floor(pos.x) + 6;
const sy = Math.floor(pos.y);
const cz = Math.floor(pos.z) + 6;
const R = 4; const TH = 12;

// === CIRCULAR BASE & WALLS ===
for (let y = sy - 1; y < sy + TH; y++) {
  for (let x = cx - R; x <= cx + R; x++) {
    for (let z = cz - R; z <= cz + R; z++) {
      let dist = Math.sqrt((x - cx) ** 2 + (z - cz) ** 2);
      if (dist <= R && dist >= R - 1) {
        let mat = y < sy + 2 ? 'mossy_cobblestone' : y < sy + 6 ? 'stone_bricks' : 'cobblestone';
        await skills.placeBlock(bot, mat, x, y, z);
      }
      if (y === sy - 1 && dist <= R) {
        await skills.placeBlock(bot, 'stone_bricks', x, y, z);
      }
      if (y === sy && dist < R - 1) {
        await skills.placeBlock(bot, 'polished_andesite', x, y, z);
      }
    }
  }
}

// === WINDOWS every 3 levels ===
for (let level = 3; level < TH; level += 3) {
  await skills.placeBlock(bot, 'glass_pane', cx + R, sy + level, cz);
  await skills.placeBlock(bot, 'glass_pane', cx - R, sy + level, cz);
  await skills.placeBlock(bot, 'glass_pane', cx, sy + level, cz + R);
  await skills.placeBlock(bot, 'glass_pane', cx, sy + level, cz - R);
}

// === DOOR ===
await skills.breakBlockAt(bot, cx, sy + 1, cz + R);
await skills.breakBlockAt(bot, cx, sy + 2, cz + R);
await skills.placeBlock(bot, 'oak_door', cx, sy + 1, cz + R);

// === CRENELLATIONS ===
for (let x = cx - R - 1; x <= cx + R + 1; x++) {
  for (let z = cz - R - 1; z <= cz + R + 1; z++) {
    let dist = Math.sqrt((x - cx) ** 2 + (z - cz) ** 2);
    if (dist <= R + 1 && dist >= R) {
      await skills.placeBlock(bot, 'stone_brick_slab', x, sy + TH, z);
      if ((x + z) % 2 === 0) {
        await skills.placeBlock(bot, 'stone_bricks', x, sy + TH + 1, z);
      }
    }
  }
}

// === CONICAL ROOF ===
for (let layer = 0; layer <= R + 1; layer++) {
  let r = R - layer;
  for (let x = cx - r; x <= cx + r; x++) {
    for (let z = cz - r; z <= cz + r; z++) {
      let dist = Math.sqrt((x - cx) ** 2 + (z - cz) ** 2);
      if (dist <= r && dist >= r - 1) {
        await skills.placeBlock(bot, 'dark_oak_stairs', x, sy + TH + 2 + layer, z, x > cx ? 'west' : 'east');
      }
    }
  }
}
await skills.placeBlock(bot, 'lightning_rod', cx, sy + TH + R + 4, cz);

// === SPIRAL STAIRCASE ===
for (let step = 0; step < TH * 2; step++) {
  let angle = (step / (TH * 2)) * Math.PI * 6;
  let stairX = Math.floor(cx + Math.cos(angle) * (R - 2));
  let stairZ = Math.floor(cz + Math.sin(angle) * (R - 2));
  await skills.placeBlock(bot, 'oak_stairs', stairX, sy + 1 + Math.floor(step / 2), stairZ, 'east');
}

// === INTERIOR ===
await skills.placeBlock(bot, 'chest', cx - 1, sy + 1, cz - 1);
await skills.placeBlock(bot, 'crafting_table', cx + 1, sy + 1, cz - 1);
await skills.placeBlock(bot, 'lantern', cx, sy + 3, cz);
await skills.placeBlock(bot, 'lantern', cx, sy + 7, cz);
await skills.placeBlock(bot, 'lantern', cx, sy + 11, cz);
await skills.placeBlock(bot, 'bed', cx - 1, sy + 1, cz + 1);
await skills.placeBlock(bot, 'bookshelf', cx + 1, sy + 1, cz + 1);
await skills.placeBlock(bot, 'bookshelf', cx + 1, sy + 2, cz + 1);
await skills.placeBlock(bot, 'red_carpet', cx, sy + 1, cz);
await skills.placeBlock(bot, 'red_carpet', cx - 1, sy + 1, cz);

// === EXTERIOR ===
await skills.placeBlock(bot, 'torch', cx - 1, sy + 2, cz + R + 1, 'side');
await skills.placeBlock(bot, 'torch', cx + 1, sy + 2, cz + R + 1, 'side');
await skills.placeBlock(bot, 'lantern', cx, sy + 3, cz + R + 1);

// Move bot outside
await skills.goToPosition(bot, cx, sy + 1, cz + R + 3, 1);
