const pos = world.getPosition(bot);
const sx = Math.floor(pos.x);
const sy = Math.floor(pos.y);
const sz = Math.floor(pos.z);
const W = 7; // width (x)
const D = 7; // depth (z)
const H = 4; // wall height

// Step 1: Cobblestone foundation
for (let x = sx; x < sx + W; x++) {
  for (let z = sz; z < sz + D; z++) {
    await skills.placeBlock(bot, 'cobblestone', x, sy - 1, z);
  }
}

// Step 2: Oak plank floor
for (let x = sx + 1; x < sx + W - 1; x++) {
  for (let z = sz + 1; z < sz + D - 1; z++) {
    await skills.placeBlock(bot, 'oak_planks', x, sy, z);
  }
}

// Step 3: Oak log corner pillars
for (let y = sy; y < sy + H; y++) {
  await skills.placeBlock(bot, 'oak_log', sx, y, sz);
  await skills.placeBlock(bot, 'oak_log', sx + W - 1, y, sz);
  await skills.placeBlock(bot, 'oak_log', sx, y, sz + D - 1);
  await skills.placeBlock(bot, 'oak_log', sx + W - 1, y, sz + D - 1);
}

// Step 4: Walls with gaps for windows and door
for (let y = sy; y < sy + H; y++) {
  for (let x = sx + 1; x < sx + W - 1; x++) {
    // Front wall (z = sz) - leave door gap at center
    let doorX = sx + Math.floor(W / 2);
    if (x === doorX && (y === sy || y === sy + 1)) continue; // door gap
    if ((y === sy + 1 || y === sy + 2) && (x === sx + 2 || x === sx + W - 3)) {
      await skills.placeBlock(bot, 'glass_pane', x, y, sz); // windows
    } else {
      await skills.placeBlock(bot, 'oak_planks', x, y, sz);
    }
    // Back wall
    if ((y === sy + 1 || y === sy + 2) && (x === sx + 2 || x === sx + W - 3)) {
      await skills.placeBlock(bot, 'glass_pane', x, y, sz + D - 1);
    } else {
      await skills.placeBlock(bot, 'oak_planks', x, y, sz + D - 1);
    }
  }
  for (let z = sz + 1; z < sz + D - 1; z++) {
    // Left wall
    if ((y === sy + 1 || y === sy + 2) && (z === sz + 2 || z === sz + D - 3)) {
      await skills.placeBlock(bot, 'glass_pane', sx, y, z);
    } else {
      await skills.placeBlock(bot, 'oak_planks', sx, y, z);
    }
    // Right wall
    if ((y === sy + 1 || y === sy + 2) && (z === sz + 2 || z === sz + D - 3)) {
      await skills.placeBlock(bot, 'glass_pane', sx + W - 1, y, z);
    } else {
      await skills.placeBlock(bot, 'oak_planks', sx + W - 1, y, z);
    }
  }
}

// Step 5: Door
await skills.placeBlock(bot, 'oak_door', sx + Math.floor(W / 2), sy, sz);

// Step 6: Peaked roof with stairs
for (let layer = 0; layer <= Math.floor(W / 2); layer++) {
  for (let z = sz - 1; z <= sz + D; z++) {
    if (sx + layer <= sx + W - 1 - layer) {
      await skills.placeBlock(bot, 'oak_stairs', sx + layer, sy + H + layer, z, 'east');
      await skills.placeBlock(bot, 'oak_stairs', sx + W - 1 - layer, sy + H + layer, z, 'west');
    }
  }
  // Fill gable triangle with planks
  for (let x = sx + layer + 1; x < sx + W - 1 - layer; x++) {
    await skills.placeBlock(bot, 'oak_planks', x, sy + H + layer, sz);
    await skills.placeBlock(bot, 'oak_planks', x, sy + H + layer, sz + D - 1);
  }
}

// Step 7: Interior
let ix = sx + 1; let iz = sz + 1;
await skills.placeBlock(bot, 'torch', ix, sy + 2, iz + 1, 'side');
await skills.placeBlock(bot, 'torch', sx + W - 2, sy + 2, iz + 1, 'side');
await skills.placeBlock(bot, 'crafting_table', ix, sy + 1, sz + D - 2);
await skills.placeBlock(bot, 'furnace', ix + 1, sy + 1, sz + D - 2);
